const BubblePage = () => {
  const [colorList, setColorList] = useState([]);
  // fetch your colors data from the server when the component mounts
  // set that data to the colorList state property
useEffect(() => {
    getData();
  });




  const getData = () => {
        // fetch protected data from the API using axiosWithAuth
