            .get("/api/colors")
            .then(res => {
                console.log(res);
                // setColorList(res);
            })

            .catch(err => console.log({ err }));
    };

  return (
    <>
      <ColorList colors={colorList} updateColors={setColorList} />
      <Bubbles colors={colorList} />
    </>
  );
};

export default BubblePage;
